// Genera una URL hacia el Recurso AJAX del Servidor
function getKMS_ajaxUri(file) {
    return sprintf(
        "%s//%s/%s/DynamicResources/Ajax/%s",
        location.protocol,
        location.host,
        $('html').data('culture-code'),
        file
    );
}

function setKMS_userPicture() {
    $('#Usuario input[type=file]').trigger('click');
}

$(function () {
    var $fileInput = $('#Usuario input[type=file]');
    $('#Usuario input[type=file]').change(function () {
        if ($fileInput.val().length < 1)
            return;
        else
            $fileInput.parent().trigger('submit');
    });
});
;/**
 * Redimensiona la barra lateral al tamaño del contenido. Debería llamarse
 * cada vez que se modifica la altura del contenido.
 */
function doKMS_redimSidebar() {
    $('#sidebar').height(
        $('#contenidos').height()
    ).css({
        visibility: 'visible'
    }).animate({
        opacity: 1
    });
}

// > Redimensionar barra lateral en cuanto haya dimensiones
$(window).ready(doKMS_redimSidebar);

$(function () {
    // Establece el idioma de jQuery DatePicker (y las Highcharts, incidentalmente)
    var lang = $('html').attr('lang');

    if ( Object.has($.datepicker.regional, lang) )
        $.datepicker.setDefaults($.datepicker.regional[lang]);
});
